import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../Utils/axiosConfig";
import CreateEventForm from "./CreateEventForm";
import EventItem from "./EventItem";

export default function Dashboard() {
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);
  const [attendeesMap, setAttendeesMap] = useState({});
  const [searchTerm, setSearchTerm] = useState("");
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    date: "",
    location: ""
  });
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      setUser(JSON.parse(userData));
    }

    axios
      .get("/api/protected")
      .then((res) => setMessage(res.data.message))
      .catch((err) => {
        setMessage("Unauthorized. Redirecting to login...");
        setTimeout(() => {
          localStorage.removeItem("token");
          localStorage.removeItem("user");
          navigate("/login");
        }, 2000);
      });

    fetchEvents();
  }, [navigate]);

 const fetchEvents = (query = "") => {
  console.log("Search submitted for:", query);
  axios
    .get(`/api/events${query ? `?search=${query}` : ""}`)
    .then((res) => {
      if (Array.isArray(res.data)) {
        setEvents(res.data); // ✅ directly use the array
      } else {
        console.warn("Unexpected response:", res.data);
        setEvents([]);
      }
    })
    .catch((err) => {
      console.error("Error fetching events", err);
      setEvents([]);
    });
};


const fetchAttendees = async (eventId) => {
  try {
    const res = await axios.get(`/api/events/${eventId}/attendees`);
    setAttendeesMap((prev) => ({
      ...prev,
      [eventId]: res.data.attendees
    }));
  } catch (err) {
    console.error("Error fetching attendees", err);
    alert("Could not load attendees.");
  }
};


  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleRegister = async (eventId) => {
  try {
    const res = await axios.post(`/api/events/${eventId}/register`);
    alert(res.data.message || "Registered successfully!");
    fetchEvents()
  } catch (err) {
    console.error("Error registering for event:", err);
    const msg = err?.response?.data?.message || "Failed to register.";
    alert(msg);
  }
};

// useEffect(() => {
//   const delayDebounce = setTimeout(() => {
//     fetchEvents(searchTerm);
//   }, 500); // waits 500ms after user stops typing

//   return () => clearTimeout(delayDebounce);
// }, [searchTerm]);


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
       const res = await axios.post("/api/events", formData);
    const newEvent = res.data.event;

setEvents((prevEvents) => Array.isArray(prevEvents) ? [...prevEvents, newEvent] : [newEvent]); 
    setFormData({ title: "", description: "", date: "" ,location: ""}); 
  } catch (err) {
    console.error("Error creating event", err);
  }
};


 return (
    <div className="p-4 max-w-3xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Welcome, {user?.name || "User"}!</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      <p className="mb-4">{message}</p>

      {/* Create Event Form Component */}
      <CreateEventForm
        formData={formData}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
      />
     <form
  onSubmit={(e) => {
    e.preventDefault();        // 🔒 Prevents page reload
    console.log("Search submitted for:", searchTerm)
    fetchEvents(searchTerm);   // 🔍 Fetches events immediately
  }}
  className="mb-4"
>
  <input
    type="text"
    placeholder="Search events by title"
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
    className="p-2 border rounded w-full"
  />
  <button
    type="submit"
    className="mt-2 bg-blue-600 text-white px-4 py-2 rounded"
  >
    Search
  </button>
</form>


      {/* Event List */}
      <div>
        <h2 className="text-xl font-semibold mb-2">All Upcoming Events</h2>
        {Array.isArray(events) && events.length === 0 ? (
          <p>No matching events found.</p>
        ) : (
          <ul className="space-y-2">
            {events.map((event) => (
              <EventItem
                key={event._id}
                event={event}
                user={user}
                handleRegister={handleRegister}
                fetchAttendees={fetchAttendees}
                attendees={attendeesMap[event._id]}
              />
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
