import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../Utils/axiosConfig";

export default function Dashboard() {
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    date: "",
    location: ""
  });
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      setUser(JSON.parse(userData));
    }

    axios
      .get("/api/protected")
      .then((res) => setMessage(res.data.message))
      .catch((err) => {
        setMessage("Unauthorized. Redirecting to login...");
        setTimeout(() => {
          localStorage.removeItem("token");
          localStorage.removeItem("user");
          navigate("/login");
        }, 2000);
      });

    fetchEvents();
  }, [navigate]);

  const fetchEvents = () => {
    axios
      .get("/api/events")
      .then((res) => setEvents(res.data.events))
      .catch((err) => console.error("Error fetching events", err));
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
       const res = await axios.post("/api/events", formData);
    const newEvent = res.data.event;

    setEvents((prevEvents) => [...prevEvents, newEvent]); 
    setFormData({ title: "", description: "", date: "" }); 
  } catch (err) {
    console.error("Error creating event", err);
  }
};

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Welcome, {user?.name || "User"}!</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      <p className="mb-4">{message}</p>

      {/* Create Event Form */}
      <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow mb-6">
        <h2 className="text-xl font-semibold mb-2">Create Event</h2>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={formData.title}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
          required
        />
        <textarea
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
        />
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
          required
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={formData.location}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Add Event
        </button>
      </form>

      {/* List of Events */}
      <div>
        <h2 className="text-xl font-semibold mb-2">My Events</h2>
        {events.length === 0 ? (
          <p>No events created yet.</p>
        ) : (
          <ul className="space-y-2">
            {events.map((event) => (
              <li key={event._id} className="p-3 border rounded shadow-sm">
                <h3 className="text-lg font-bold">{event.title}</h3>
                <p>{event.description}</p>
                <p className="text-sm text-gray-500">📅 {new Date(event.date).toLocaleDateString()}</p>
                <p className="text-sm text-gray-500">
                 📍 Location: {event.location}
                </p>  
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
