import { useState } from "react";
import AttendeeList from "./AttendeeList";

export default function EventItem({
  event,
  user,
  handleRegister,
  fetchAttendees,
  attendees
}) {
  const isOwner = String(event.user?._id || event.user) === String(user?._id);
  const [showAttendees, setShowAttendees] = useState(false);

  const handleToggleAttendees = () => {
    if (!showAttendees && !attendees) {
      fetchAttendees(event._id); // Only fetch if not fetched already
    }
    setShowAttendees(!showAttendees);
  };

  return (
    <li className="p-3 border rounded shadow-sm">
      <h3 className="text-lg font-bold">{event.title}</h3>
      <p>{event.description}</p>
      <p className="text-sm text-gray-500">
        Date: {new Date(event.date).toLocaleDateString()}
      </p>
      <p className="text-sm text-gray-500">üìç Location: {event.location}</p>
      <p className="text-sm text-gray-600">
        üéüÔ∏è {event.attendees?.length || 0} attendees
      </p>

      <button
        className="mt-1 bg-gray-500 text-white px-2 py-1 rounded"
        onClick={handleToggleAttendees}
      >
        {showAttendees ? "Hide Attendees" : "View Attendees"}
      </button>

      {showAttendees && attendees && <AttendeeList attendees={attendees} />}

      {!isOwner && (
        <button
          onClick={() => handleRegister(event._id)}
          className="mt-2 bg-green-500 text-white px-3 py-1 rounded"
        >
          Register
        </button>
      )}
    </li>
  );
}
