import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../Utils/axiosConfig";

export default function Dashboard() {
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);
  const [attendees, setAttendees] = useState([]);
  const [attendeesMap, setAttendeesMap] = useState({});
  const [selectedEventId, setSelectedEventId] = useState(null);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    date: "",
    location: ""
  });
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      setUser(JSON.parse(userData));
    }

    axios
      .get("/api/protected")
      .then((res) => setMessage(res.data.message))
      .catch((err) => {
        setMessage("Unauthorized. Redirecting to login...");
        setTimeout(() => {
          localStorage.removeItem("token");
          localStorage.removeItem("user");
          navigate("/login");
        }, 2000);
      });

    fetchEvents();
  }, [navigate]);

 const fetchEvents = () => {
  axios
    .get("/api/events")
    .then((res) => {
      if (Array.isArray(res.data)) {
        setEvents(res.data); // ✅ directly use the array
      } else {
        console.warn("Unexpected response:", res.data);
        setEvents([]);
      }
    })
    .catch((err) => {
      console.error("Error fetching events", err);
      setEvents([]);
    });
};


const fetchAttendees = async (eventId) => {
  try {
    const res = await axios.get(`/api/events/${eventId}/attendees`);
    setAttendeesMap((prev) => ({
      ...prev,
      [eventId]: res.data.attendees
    }));
  } catch (err) {
    console.error("Error fetching attendees", err);
    alert("Could not load attendees.");
  }
};


  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleRegister = async (eventId) => {
  try {
    const res = await axios.post(`/api/events/${eventId}/register`);
    alert(res.data.message || "Registered successfully!");
    fetchEvents()
  } catch (err) {
    console.error("Error registering for event:", err);
    const msg = err?.response?.data?.message || "Failed to register.";
    alert(msg);
  }
};


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
       const res = await axios.post("/api/events", formData);
    const newEvent = res.data.event;

setEvents((prevEvents) => Array.isArray(prevEvents) ? [...prevEvents, newEvent] : [newEvent]); 
    setFormData({ title: "", description: "", date: "" ,location: ""}); 
  } catch (err) {
    console.error("Error creating event", err);
  }
};

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Welcome, {user?.name || "User"}!</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      <p className="mb-4">{message}</p>

      {/* Create Event Form */}
      <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow mb-6">
        <h2 className="text-xl font-semibold mb-2">Create Event</h2>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={formData.title}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
          required
        />
        <textarea
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
        />
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
          required
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={formData.location}
          onChange={handleChange}
          className="block w-full mb-2 p-2 border rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Add Event
        </button>
      </form>

      {/* List of Events */}
      <div>
        <h2 className="text-xl font-semibold mb-2">All Upcoming Events</h2>
        {Array.isArray(events) && events.length === 0 ? (
  <p>No events created yet.</p>
) : (
  <ul className="space-y-2">
    {events?.map((event) => (
      <li key={event._id} className="p-3 border rounded shadow-sm">
        <h3 className="text-lg font-bold">{event.title}</h3>
        <p>{event.description}</p>
        <p className="text-sm text-gray-500">
          Date: {new Date(event.date).toLocaleDateString()}
        </p>
        <p className="text-sm text-gray-500">
          📍 Location: {event.location}
        </p>
        <p className="text-sm text-gray-600">
       🎟️ {event.attendees?.length || 0} attendees
         </p>
         <button
  className="mt-1 bg-gray-500 text-white px-2 py-1 rounded"
  onClick={() => fetchAttendees(event._id)}
>
  View Attendees
</button>
{selectedEventId && (
  <div className="mt-6 p-4 bg-gray-100 rounded">
    <h3 className="text-lg font-semibold mb-2">Attendees:</h3>
    {attendees.length === 0 ? (
      <p>No attendees yet.</p>
    ) : (
      <ul className="list-disc ml-6">
        {attendees.map((a) => (
          <li key={a._id}>
            {a.name} ({a.email})
          </li>
        ))}
      </ul>
    )}
  </div>
)}
         {String(event.user?._id || event.user) !== String(user?._id) &&  (
        <button
          onClick={() => handleRegister(event._id)}
          className="mt-2 bg-green-500 text-white px-3 py-1 rounded"
        >
          Register
        </button>
        
         )}
      </li>
    ))}
  </ul>
)}

      </div>
    </div>
  );
}
